/* Increment interface stat */
#define WLCIFCNTINCR(_scb, _stat)					\
	if (_scb) {							\
		if (SCB_WDS(_scb)) {					\
			WLCNTINCR(_scb->wds->_cnt._stat);		\
		} else {						\
			wlc_bsscfg_t *_bsscfg = SCB_BSSCFG(_scb);	\
			if (_bsscfg)					\
				WLCNTINCR(_bsscfg->wlcif->_cnt._stat);	\
		}							\
	}

