int
auto_conn_cmp_bytes(uchar *arg1, uchar *arg2, uint8 nbytes)
{
	int i;

	for (i = nbytes - 1; i >= 0; i--) {
		if (toupper(arg1[i]) != toupper(arg2[i]))
			return (arg1[i] - arg2[i]);
	}
	return 0;
}


struct prev_mac{
	int flag;
	struct ether_addr mac_addr;
};
#define PREV_MAC_ARRAY_LEN 10
struct prev_mac prev_mac_addr_array[PREV_MAC_ARRAY_LEN] = {0};
static int add_num = 0;

void init_array()
{
	int i;

	for(i = 0; i < PREV_MAC_ARRAY_LEN; i++)
		prev_mac_addr_array[i].flag = 0;
}

int insert_array(struct ether_addr *mac)
{
	int i;
	int add_time = 0;
	int find_flag = 0;

	for(i = 0; i < PREV_MAC_ARRAY_LEN; i++)
	{
		if(prev_mac_addr_array[i].flag == 0)
		{
			memcpy(&(prev_mac_addr_array[i].mac_addr), mac, 6);
			prev_mac_addr_array[i].flag = 1;
			break;
		}
		else if(memcmp(&(prev_mac_addr_array[i].mac_addr), mac, 6) == 0)
		{
			find_flag = 1;
			break;
		}
	}

	if(i == PREV_MAC_ARRAY_LEN)
	{
		add_time = (add_num++)%PREV_MAC_ARRAY_LEN;
		memcpy(&(prev_mac_addr_array[add_time].mac_addr), mac, 6);
	}

	return find_flag;

}
