		if (p_len < ETHER_MIN_LEN || p_len > ETHER_MAX_LEN || params == NULL) {
			err = BCME_BADARG;
			break;
		}
		pkt = PKTGET(osh, p_len + TXOFF, TRUE);
		if (pkt == NULL) {
			err = BCME_NOMEM;
			break;
		}

		PKTPULL(osh, pkt, TXOFF);
		bcopy(params, PKTDATA(osh, pkt), p_len);
		PKTSETLEN(osh, pkt, p_len);
		status = wlc_sendpkt(wlc, pkt, wlcif);
		if (status)
			err = BCME_NORESOURCE;
		break;
