static void save_probe_info(struct ether_addr *mac)
{
	char mac_str[32] = {0};
	int index = 0;
	
	sprintf(mac_str,"%02x:%02x:%02x:%02x:%02x:%02x",
			((uint8 *)(mac))[0],((uint8 *)(mac))[1],((uint8 *)(mac))[2],
			((uint8 *)(mac))[3],((uint8 *)(mac))[4],((uint8 *)(mac))[5]);
	
//	diag_printf("receive probe request, mac[%s]\n",mac_str);
	for( index = 0 ; index < MAX_PROBE_INFO_NUM; index++)
	{
		if(strcmp(mac_str,wlan_probe_info[index].mac) == 0)
		{
			return ;
		}
		if(strlen(wlan_probe_info[index].mac) < MIN_ETHADDR_LEN)
		{
			break;
		}		
	}
	if(MAX_PROBE_INFO_NUM == index)//table ful
	{
		strncpy(wlan_probe_info[index-1].mac, mac_str, 32);
		wlan_probe_info[index-1].is_reported = 0;
		return ;
	}
	strncpy(wlan_probe_info[index].mac, mac_str, 32);
	wlan_probe_info[index].is_reported = 0;
	return ;		
}
