static const char*
wlc_lookup_name(const wlc_id_name_table_t tbl, int id)
{
	const struct wlc_id_name_entry *elt = tbl;
	static char unknown[16];

	for (elt = tbl; elt->name != NULL; elt++) {
		if (id == elt->id)
			return elt->name;
	}

	snprintf(unknown, sizeof(unknown), "ID:%d", id);

	return unknown;
}
