/* reset the TSF register to the given value */
static void
wlc_tsf_set(wlc_info_t *wlc, uint32 tsf_l, uint32 tsf_h)
{
	osl_t *osh;
	osh = wlc->osh;

	W_REG(osh, &wlc->regs->tsf_timerlow, tsf_l);
	W_REG(osh, &wlc->regs->tsf_timerhigh, tsf_h);
}
